mapper.select:

	SELECT * FROM member
	WHERE 
		t_id=#{t_id} 
	AND 
		t_pass=#{t_pass}

mapper.update:
 	
		UPDATE trainer 
		SET 
			t_intro = #{t_intro}
			,t_postcode = #{t_postcode}
			,t_address = #{t_address}
			,t_detailAdress = #{t_detailAddress}
			,photo_name = #{photo_name}
			,t_exp = #{t_exp}
			,t_sns = #{T_sns}
			,t_program = #{t_program}
		WHERE
			t_id=#{t_id}
